package com.example.easytime101;import android.app.Activity;import android.graphics.Color;import android.util.Log;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.BaseAdapter;import android.widget.CheckBox;import android.widget.EditText;import android.widget.ImageView;import android.widget.RadioButton;import android.widget.RadioGroup;import android.widget.TextView;import java.util.List;import java.util.Stack;public class CustomAdapter extends BaseAdapter {    Activity activity;    List<UserModel> users1;    LayoutInflater inflater;    List<Boolean> checkIfdone;    public interface OnCheckClickListener {        void onItemClick(int position);    }    public CustomAdapter(Activity activity) {        this.activity = activity;    }    public CustomAdapter(List<UserModel> users, Activity activity) {        this.users1 = users;        this.activity = activity;        inflater = activity.getLayoutInflater();    }    @Override    public int getCount() {        if (users1!=null)        return users1.size();        return 0;    }    @Override    public Object getItem(int i) {        return i;    }    @Override    public long getItemId(int i) {        return i;    }    @Override    public View getView(int i, View view1, ViewGroup viewGroup) {        viewHolder holder = null;        if (view1 == null) {            view1 = inflater.inflate(R.layout.list_view_item, viewGroup, false);            holder = new viewHolder();            holder.tvtitle = (TextView) view1.findViewById(R.id.tvtitle);            holder.ivCheckBox = (ImageView) view1.findViewById(R.id.iv_check_box);            view1.setTag(holder);        } else            holder = (viewHolder) view1.getTag();        UserModel model = users1.get(i);        holder.tvtitle.setText(model.getUsername());        holder.ivCheckBox.setImageResource(model.getImageResource());        holder.tvtitle.setBackgroundResource(model.getTitleResource());        if (model.getUrgent())            holder.tvtitle.setTextColor(Color.RED);        if (model.getImportant())            holder.tvtitle.setTextColor(Color.YELLOW);        if (model.getWhatWas()!=null) {            if (model.getWhatWas().equals("important"))                holder.tvtitle.setTextColor(Color.YELLOW);            if (model.getWhatWas().equals("urgent"))                holder.tvtitle.setTextColor(Color.RED);        }        return view1;    }    public void updateList(List<UserModel> users) {        users = this.users1;    }    public void updateRecords(List<UserModel> users) {        this.users1 = users;        notifyDataSetChanged();    }    public void changeBackground (viewHolder holder, List<UserModel> users) {    }}class viewHolder{    TextView tvtitle;    ImageView ivCheckBox;}